import React, { useState } from 'react';

const api = {
  key: "f86bb73e5e12b7c8a46fc1ffbd46962f",
  base: "https://api.openweathermap.org/data/2.5/"
}

function App() { 

  const [query, setQuery] = useState('');
  const [clima, setclima] = useState('');

  const criaData = (d) => {
    let meses = ["Janeiro", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    let dias = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    let dia = dias[d.getDay()];
    let data = d.getDate();
    let mes = meses[d.getMonth()];
    let ano = d.getFullYear();

    return `${dia} ${data} ${mes} ${ano}`
  }

  return (
    <div className="clima app">
      <main>
        <div className="caixa-de-pesquisa">
          <input
          type="text"
          className="barra-de-pesquisa"
          placeholder="Busque..."
          />
          <div className="caixa-de-localizacao">
            <div className="localizacao">São Leopoldo, RS, Brasil</div>
            <div className="data">{criaData(new Date())}</div>
            </div>
        </div>
        <div className="caixa-do-clima">
          <div className="temp">
              33ºC
            </div>
            <div className="clima">Ensolarado</div>
        </div>
      </main>
    </div>
  );
}

export default App;
